CREATE TABLE plan (
  id SERIAL PRIMARY KEY NOT NULL,
  name VARCHAR(255) UNIQUE NOT NULL,
  user_id INT NOT NULL REFERENCES users (id),
  topic VARCHAR(255) NOT NULL,
  topic_en VARCHAR(255) NOT NULL,
  topic_short VARCHAR(64) NOT NULL,
  readiness_willingness TEXT,
  readiness_willingness_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  readiness_willingness_updated_by VARCHAR(16),
  ir_goal_type VARCHAR(64),
  ir_goal_type_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  ir_goal_type_updated_by VARCHAR(16),
  ir_goal_details TEXT,
  ir_goal_details_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  ir_goal_details_updated_by VARCHAR(16),
  proposed_activity TEXT,
  proposed_activity_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  proposed_activity_updated_by VARCHAR(16),
  plan_note TEXT,
  plan_note_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  plan_note_updated_by VARCHAR(16),
  contact_person TEXT,
  contact_person_updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  contact_person_updated_by VARCHAR(16),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT now() NOT NULL,
  updated_by VARCHAR(16),
  for_admin BOOLEAN DEFAULT false
);

INSERT 
INTO plan (
  name, user_id, topic, topic_en, topic_short, readiness_willingness, readiness_willingness_updated_by,
  ir_goal_type, ir_goal_type_updated_by, ir_goal_details, ir_goal_details_updated_by,
  proposed_activity, proposed_activity_updated_by, plan_note, plan_note_updated_by,
  contact_person, contact_person_updated_by, updated_by
) 
VALUES 
('PLAN1', 1, 'แผนควบคุมยาสูบ', 'Tobacco Control', 'Tobacco',
'ready 1',  'user', 'type_1',  'user', 
'เสริมสร้างการผลักดันนโยบายระดับชาติของประเทศไทยในการควบคุมยาสูบ ผ่านการบูรณาการแนวทางการควบคุมยาสูบจากทั่วโลก และเสริมสร้างการปฏิบัติตามอนุสัญญาขององค์การอนามัยโลกว่าด้วยการควบคุมยาสูบ (WHO FCTC) โดยเน้นที่ความท้าทายใหม่ ๆ เช่น บุหรี่ไฟฟ้า',  'user', 
'<ol>
<li dir="ltr" role="presentation"><strong>การติดตามนโยบายและการวิเคราะห์เชิงกลยุทธ์ :</strong> ติดตามนโยบายการควบคุมยาสูบทั่วโลกอย่างสม่ำเสมอ โดยเน้นที่การพัฒนาที่เกี่ยวข้องกับบุหรี่ไฟฟ้า วิเคราะห์แนวโน้ม และแนวทางในการควบคุมจากนานาประเทศเพื่อปรับปรุงการวางแผนในเชิงกลยุทธ์ เพื่อสนับสนุนแนวทางในการพัฒนาของสำนักงานกองทุนสนับสนุนการสร้างเสริมสุขภาพ</li>
<li dir="ltr" role="presentation"><strong>ความร่วมมือระหว่างประเทศและการแลกเปลี่ยนความรู้ :</strong> เข้าร่วมเครือข่ายและพันธมิตรการควบคุมยาสูบระหว่างประเทศ เพื่อแลกเปลี่ยนข้อมูลเชิงลึกเกี่ยวกับกลยุทธ์ในการกำกับดูแลและการควบคุมบุหรี่ไฟฟ้าอย่างมีประสิทธิภาพ</li>
<li dir="ltr" role="presentation"><strong>การวิจัยเพื่อเปรียบเทียบนโยบาย :</strong> ใช้ประโยชน์จากความร่วมมือระหว่างประเทศของสำนักงานกองทุนสนับสนุนการสร้างเสริมสุขภาพ ในการวิจัยเปรียบเทียบนโยบายบุหรี่ไฟฟ้าและประสิทธิผลของนโยบายนั้น ๆ ในการปรับปรุงสุขภาพของประชาชน และใช้ผลการวิจัยเหล่านี้เพื่อสนับสนุนการตัดสินใจเชิงนโยบายโดยอิงหลักฐานในประเทศไทย</li>
<li dir="ltr" role="presentation"><strong>การปรับใช้แนวปฏิบัติที่ดีที่สุด :</strong> ระบุและปรับใช้กลยุทธ์การควบคุมยาสูบที่ประสบความสำเร็จจากทั่วโลก โดยปรับให้เหมาะสมกับบริบทและสภาพแวดล้อมที่มีลักษณะเฉพาะของประเทศไทย</li>
<li dir="ltr" role="presentation"><strong id="docs-internal-guid-f5fde012-7fff-d61b-a1d5-03ec754975b6">การผลักดันและการปฏิบัติตาม FCTC :</strong> ผลักดันการปฏิบัติตามแนวทางของ FCTC ในระดับประเทศ โดยมุ่งเน้นไปที่การควบคุมบุหรี่ไฟฟ้า และผลิตภัณฑ์ยาสูบใหม่ ๆ</li>
</ol>', 'user',
